    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
        
        String savi1=sav1.getText();
        int sav1 = Integer.parseInt(savi1);

        String savi2=sav2.getText();
        int sav2 = Integer.parseInt(savi2);

        String savi3=sav3.getText();
        int sav3 = Integer.parseInt(savi3);

        String savi4=sav4.getText();
        int sav4 = Integer.parseInt(savi4);

        String savi5=sav5.getText();
        int sav5 = Integer.parseInt(savi5);

        double total;

        total = sav1+sav2+sav3+sav4+sav5;

        String totale = String.valueOf(total);
        savf.setText(totale);
        savy.setText(totale);
        
        String savre = savr.getText();
        double sav;
        sav  = Double.parseDouble(savre);
        if(total == sav ){
            savsum.setText("Your savings is good!!\nCarry on");
        }
        else if(total > sav){
             savsum.setText("Your savings is fabulous!!\n Best wishes");
        }
        else{
             savsum.setText("You should concentrate on your savings");
        }
    }//GEN-LAST:event_jButton2ActionPerformed

