    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        next n = new next();
        n.setVisible(true);
        
           
        String pertot = totin.getText();
        n.totin.setText(pertot);
        int pertotal = Integer.parseInt(pertot);
        double pertra = 0.09*pertotal;
        String pertravel = String.valueOf(pertra);
        n.travr.setText(pertravel);
        
        
        double peredu = 0.15*pertotal;
        String pereduc =  String.valueOf(peredu);
        n.edur.setText(pereduc);
        
        dispose();
    }//GEN-LAST:event_jButton1ActionPerformed

